import React, { useEffect } from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import { TextField, Button, List, ListItem, ListItemText, Paper } from '@material-ui/core';
import { IconButton } from '@material-ui/core';
import PhotoCamera from '@material-ui/icons/PhotoCamera';
const useStyles = makeStyles((theme) => ({
  chatWindow: {
    display: 'flex',
    flexDirection: 'column',
    height: '80vh',
    maxWidth: '600px',
    margin: 'auto',
    border: `1px solid ${theme.palette.divider}`,
    borderRadius: theme.shape.borderRadius,
    overflow: 'hidden',
    backgroundColor: '#E5DDD5',
  },
  messageArea: {
    flexGrow: 1,
    overflowY: 'auto',
    padding: theme.spacing(2),
  },
  messageInput: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(1),
    backgroundColor: '#F0F0F0',
  },
  inputField: {
    flexGrow: 1,
    marginRight: theme.spacing(1),
  },
  messageBubble: {
    maxWidth: '60%',
    margin: '5px',
    padding: '10px',
    borderRadius: '10px',
  },
  senderBubble: {
    marginLeft: 'auto',
    backgroundColor: '#DCF8C6',
    textAlign: 'right',
  },
  receiverBubble: {
    marginRight: 'auto',
    backgroundColor: '#FFFFFF',
    textAlign: 'left',
  },
}));

const ChatWindow = (props) => {
  const classes = useStyles();
  const {socket, userId, friendId, sendMessage, message, setMessage, refetch, setChat, chat, decryptMessage, publicKey, myPublicKey } = props;

  const fetchChat = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/api/chat/${userId}/${friendId}`);
      let arr = response.data.map(item => ({ message: item.message, sender: item.sender }));
      setChat(arr);
    } catch (error) {
      console.error("Error fetching chat history:", error);
    }
  };


  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Optional: Set state to show image preview
      // ...

      // Prepare to send this file
      const formData = new FormData();
      formData.append('image', file);

      // Send the image to the server
      axios.post('http://localhost:3001/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      })
      .then(response => {
        // Handle successful upload
        // Get the path of the uploaded image
        const imagePath = response.data.filePath;

        // Now send this imagePath as a message through the socket
        const imageMessage = {
          // You need to define the structure of this message based on your application logic
          // It should include the sender, receiver, and the image path or URL
          image : imagePath,
          message : ""
        };

        socket.emit('chat message', imageMessage); // Assuming you have a socket instance
      })
      .catch(error => {
        // Handle error
        console.error('Error uploading image:', error);
      });
    }
  };



  useEffect(() => {
    fetchChat();
  }, [refetch]);

  return (
    <Paper className={classes.chatWindow}>
      <List className={classes.messageArea}>
        {chat?.map((msg, index) => (
          <ListItem key={index} className={`${classes.messageBubble} ${msg.sender === userId ? classes.senderBubble : classes.receiverBubble}`}>
            <ListItemText primary={decryptMessage(msg.message, msg.sender === userId ? publicKey : publicKey)} />
          </ListItem>
        ))}
      </List>
      <form className={classes.messageInput} onSubmit={sendMessage}>

      <input
      accept="image/*"
      style={{ display: 'none' }}
      id="icon-button-file"
      type="file"
      onChange={handleImageChange}
    />
    <label htmlFor="icon-button-file">
      <IconButton color="primary" component="span">
        <PhotoCamera />
      </IconButton>
    </label>
        <TextField
          label="Type a message"
          variant="outlined"
          size="small"
          className={classes.inputField}
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <Button variant="contained" color="primary" type="submit">
          Send
        </Button>
      </form>
    </Paper>
  );
};

export default ChatWindow;
