import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import { Typography, List, ListItem, ListItemText, TextField, Button } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: theme.spacing(2),
    padding : 50
  },
  friendsList: {
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  },
  addFriend: {
    marginTop: theme.spacing(0),
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    gap: "10px"
  },
}));

const ListOfFriends = (props) => {
  const classes = useStyles();
  const [friends, setFriends] = useState([]);
  const userId = localStorage.getItem('userId');
  const [newFriendId, setNewFriendId] = useState('');

  useEffect(() => {
    axios
      .get(`http://localhost:3001/api/users/${userId}/friends`)
      .then((response) => {
        setFriends(response.data);
      })
      .catch((error) => console.error('Error fetching friends:', error));
  }, []);

  const handleAddFriend = () => {
    axios
      .post(`http://localhost:3001/api/users/${userId}/add-friend`, { friendId: newFriendId })
      .then(() => {
        // Optionally, re-fetch friends list or update UI
        console.log('Friend added successfully');
      })
      .catch((error) => console.error('Error adding friend:', error));
  };

  return (
    <div className={classes.root}>
        <div style={{ display : "flex", justifyContent : "space-evenly"}}>

      <div className={classes.addFriend}>
        {/* <Typography variant="h6">Add a New Friend</Typography> */}
        <TextField
          type="text"
          label="Enter friend's user ID"
          variant="outlined"
          value={newFriendId}
          onChange={(e) => setNewFriendId(e.target.value)}
          fullWidth
          margin="normal"
        />
        <Button variant="contained" color="primary" onClick={handleAddFriend}>
          Add Friend
        </Button>
      </div>
        </div>
      <List className={classes.friendsList}>
      <Typography variant="h4">My Friends</Typography>

        {friends.map((friend) => (
          <ListItem key={friend._id} onClick={() => props.openingNewWIndow(friend._id)} button>
            <ListItemText primary={friend.username} />
          </ListItem>
        ))}
      </List>

    </div>
  );
};

export default ListOfFriends;
