import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import nacl from 'tweetnacl';
import naclUtil from 'tweetnacl-util';
import axios from 'axios';

const socket = io.connect('http://localhost:3001');

function Chat() {
    const [message, setMessage] = useState('');
    const [chat, setChat] = useState([]);
    const [recipientPublicKey, setRecipientPublicKey] = useState('');

    useEffect(() => {
        socket.on('chat message', (encryptedMsg) => {
            try {
                const decryptedMsg = decryptMessage(encryptedMsg);
                setChat([...chat, decryptedMsg]);
            } catch (error) {
                console.error("Decryption error", error);
            }
        });
    }, [chat]);

    const sendMessage = async (e) => {
        e.preventDefault();

        // Fetch recipient's public key from the server
        // Assuming you have a function to get the recipient's username or ID
        const recipientUsername = '65a42345de580c62df9cdf31'; // Replace with actual recipient's username
        const response = await axios.get(`http://localhost:3001/api/users/${recipientUsername}/public-key`);
        setRecipientPublicKey(response.data.publicKey);

        // Encrypt the message
        const encryptedMsg = encryptMessage(recipientPublicKey, message);
        socket.emit('chat message', encryptedMsg);
        setMessage('');
    };

    // Encrypt function
    const encryptMessage = (publicKey, message) => {
        const nonce = nacl.randomBytes(nacl.box.nonceLength);
        const encryptedMessage = nacl.box(
            naclUtil.decodeUTF8(message),
            nonce,
            naclUtil.decodeBase64(publicKey),
            naclUtil.decodeBase64(localStorage.getItem('privateKey'))
        );
        return { nonce: naclUtil.encodeBase64(nonce), encryptedMessage: naclUtil.encodeBase64(encryptedMessage) };
    };

    // Decrypt function
    const decryptMessage = (encryptedMsg) => {
        const { nonce, encryptedMessage } = encryptedMsg;
        const decryptedMessage = nacl.box.open(
            naclUtil.decodeBase64(encryptedMessage),
            naclUtil.decodeBase64(nonce),
            naclUtil.decodeBase64(recipientPublicKey),
            naclUtil.decodeBase64(localStorage.getItem('privateKey'))
        );
        return decryptedMessage ? naclUtil.encodeUTF8(decryptedMessage) : 'Failed to decrypt message';
    };

    return (
        <div>
            <h2>Chat</h2>
            <div className="chat-box">
                {chat.map((msg, index) => (
                    <p key={index}>{msg}</p>
                ))}
            </div>
            <form onSubmit={sendMessage}>
                <input type="text" placeholder="Message" value={message} onChange={(e) => setMessage(e.target.value)} />
                <button type="submit">Send</button>
            </form>
        </div>
    );
}

export default Chat;
