import React, { useState, useEffect } from 'react';
import axios from 'axios';
const ListOfFriends = () => {
    const [friends, setFriends] = useState([]);
    const userId = localStorage.getItem("userId") ;
    const [newFriendId, setNewFriendId] = useState('');

    // Replace with the actual user ID
    // Additional state for handling new friend ID
    useEffect(() => {
        if(!userId){
            return 
        } 
        axios.get(`http://localhost:3001/api/users/${userId}/friends`)
            .then(response => {
                setFriends(response.data);
            })
            .catch(error => console.error("Error fetching friends:", error));
    }, []);

    const handleAddFriend = () => {
        axios.post(`http://localhost:3001/api/users/${userId}/add-friend`, { friendId: newFriendId })
            .then(() => {
                // Optionally, re-fetch friends list or update UI
                console.log('Friend added successfully');
            })
            .catch(error => console.error("Error adding friend:", error));
    };


  return (
    <div>
        <h2>My Friends</h2>
        <ul>
            {friends.map(friend => (
                <li key={friend._id}>{friend.username}</li>
            ))}
        </ul>
        <div>
            <h3>Add a New Friend</h3>
            <input 
                type="text" 
                placeholder="Enter friend's user ID" 
                value={newFriendId} 
                onChange={e => setNewFriendId(e.target.value
)}
/>
<button onClick={handleAddFriend}>Add Friend</button>
</div>

    </div>
  )
}

export default ListOfFriends
